import React, { useEffect, useContext, useState } from "react";
import ViajeContext from "../../context/ViajesContext";
import { Col, Container, Form, Row, Button } from "react-bootstrap";
import Select from "react-select";
const objnew = {
  pk: null,
  hora: "",
  omnibus: "",
  chofer1: "",
  chofer2: "",
  origen: "",
  destino: "",
  create_by: Number(1),
};

const FormViajes = () => {


  return (
    <>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Hora</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  name="hora"
                  value={form.hora}
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Numero del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  name="omnibus"
                  onChange={handleChange}
                >
                  <option> ----- </option>
                  {omnibus.map((omnibu) => (
                    <option
                      value={omnibu.pk}
                      key={omnibu.pk}
                      selected={omnibu.pk === form.omnibus}
                    >
                      {omnibu.numero}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Chofer 1 </label>
                <Form.Control
                  placeholder="Nombre del Chofer 1."
                  as="select"
                  name="chofer1"
                  id="chofer1"
                  onChange={handleChange}
                >
                  <option>------</option>
                  {choferes.map((chofers) => (
                    <option
                      key={chofers.pk}
                      value={chofers.pk}
                      selected={chofers.pk === form.chofer1}
                    >
                      {chofers.nombre}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Chofer 2</label>
                <Form.Control
                  placeholder="Nombre del Chofer 2."
                  as="select"
                  name="chofer2"
                  id="chofer2"
                  onChange={handleChange}
                >
                  <option> ----- </option>
                  {choferes.map((chofer) => (
                    <option
                      key={chofer.pk}
                      value={chofer.pk}
                      selected={chofer.pk === form.chofer2}
                    >
                      {chofer.nombre}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Origen</label>
                <Form.Control
                  placeholder="Origen"
                  as="select"
                  name="origen"
                  id="origen"
                  onChange={handleChange}
                >
                  <option> ----- </option>
                  {destinos.map((destino, index) => (
                    <option
                      key={destino.pk}
                      value={destino.pk}
                      selected={destino.pk === form.origen}
                    >
                      {destino.nombre}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Destino</label>
                <Form.Control
                  placeholder="Destino"
                  as="select"
                  name="destino"
                  onChange={handleChange}
                >
                  <option> ----- </option>
                  {destinos.map((destino) => (
                    <option
                      key={destino.pk}
                      value={destino.pk}
                      selected={destino.pk === form.destino}
                    >
                      {destino.nombre}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Button
              className="btn-fill ml-auto mr-2"
              variant="danger"
              onClick={() => handleReset()}
            >
              Cancelar
            </Button>
            <Button className="btn-fill " type="submit" variant="info">
              Acceptar
            </Button>
          </Row>
        </Form>
      </Container>
    </>
  );
};

export default FormViajes;
