import React, { useState, useEffect, useContext } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { Container, Button, Form, Col, Row, Breadcrumb } from "react-bootstrap";
import "./grid.css";
import { useHistory } from "react-router-dom";
import servicesviajes from "modules/Viajes/services";
import ReservacionContext from "context/ReservacionesContext";

let reserva = {
  fecha: "",
  viajes: "",
  create_by: Number(1),
  members: [1],
  cis: "",
  asientos: "",
};
const RealizarReservacion = () => {
  const [res, setRes] = useState(reserva);
  const [cis, setCis] = useState([]);
  const [visible, setVisible] = useState(true);
  const { cant } = useParams();
  const state = useSelector((state) => state);
  const { cantidad } = state.reservacion;
  //const { viajes } = state.viajes;
  const dispatch = useDispatch();

  const { viajes, loadViajes, postReservaciones,setOpenModal } =
    useContext(ReservacionContext);
  // console.log("ver res-viajes", viajes, loadViajes());

  console.log("ver cantidad", cantidad);
  let history = useHistory();

  const Regresar = () => {
    history.push("/admin/reservar");
  };

  useEffect(() => {
    loadViajes();
  }, []);

  const handleChangeCIS = (e) => {
    e.preventDefault();
    cis.push(e.target.value);
  };

  const handleChange = (e) => {
    res.asientos = Number(cant);

    setRes({
      ...res,
      [e.target.name]: e.target.value,
    });
  };

  const hanledsubmit = (e) => {
    e.preventDefault();
    console.log("se envio el submit", cis);
    res.cis = cis.join(",");
    console.log("se envio el submit", res);
    //console.log("ver viajes", viajes);
    postReservaciones(res);
    history.push("/admin/reservaciones")
  };
  return (
    <Container>
      <Breadcrumb>
        <Breadcrumb.Item href="#">Home</Breadcrumb.Item>
        <Breadcrumb.Item href="https://getbootstrap.com/docs/4.0/components/breadcrumb/">
          Libra
        </Breadcrumb.Item>
        <Breadcrumb.Item active>Data</Breadcrumb.Item>
      </Breadcrumb>
      <div>
        <p>
          <b>{`Desean Viajar ${cant} personas.`}</b>
        </p>
      </div>
      <Form onSubmit={hanledsubmit}>
        <Row>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Fecha</b>
              </Form.Label>
              <Form.Control
                type="date"
                name="fecha"
                value={res.fecha}
                onChange={handleChange}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          </Col>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Viaje</b>
              </Form.Label>
              <Form.Control
                as="select"
                name="viajes"
                onChange={handleChange}
                placeholder="Entre La fecha"
              >
                <option value="">------</option>
                {viajes.map((viaje, index) => (
                  <option key={index} value={viaje.pk}>
                    {`${viaje.origen}- ${viaje.destino}`}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
        </Row>

        <div className="grid">
          {cantidad.map((cant, index) => (
            <Form.Group key={index}>
              <Form.Label className="label">
                <b>CI {index + 1}:</b>
              </Form.Label>
              <Form.Control
                name="cis"
                onChange={handleChangeCIS}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          ))}
        </div>
        <Row>
          <Button
            // className="btn-fill ml-auto mr-2"
            bsStyle="success"
            //onClick={setModalClose}
            onClick={() => Regresar()}
          >
            Cancelar
          </Button>{" "}
          <Button className=" btn btn-fill " type="submit" variant="info">
            Acceptar
          </Button>
        </Row>
      </Form>
    </Container>
  );
};

export default RealizarReservacion;
