import {
  LOAD_VIAJES,
  CREATE_VIAJES,
  UPDATE_VIAJES,
  DELETE_VIAJES,
  CLEAR_VIAJES,
} from "../types/index";

export const initialstate = {
  viajes: [],
};

export default (state = initialstate, action) => {
  switch (action.type) {
    case LOAD_VIAJES: {
      return {
        ...state,
        viajes: action.payload.map((viajes) => viajes),
      };
    }
    case CREATE_VIAJES: {
      return {
        ...state,
        viajes: [...state.viajes, action.payload],
      };
    }

    case UPDATE_VIAJES: {
      let newData = state.viajes.map((viaje) =>
        viaje.pk === action.payload.pk ? action.payload : viaje
      );
      return {
        ...state,
        viajes: newData,
      };
    }
    case DELETE_VIAJES: {
      let newDataDel = state.viajes.filter(
        (viaje) => viaje.pk !== action.payload.pk
      );
      return {
        ...state,
        viajes: newDataDel,
      };
    }
    case CLEAR_VIAJES: {
      return initialstate;
    }
    default:
      state;
  }
};
