import {useState,createContext} from "react"
import { useDispatch } from 'react-redux';
import services from "components/Login/services";
import  storage  from 'helpers/storage';
import jwt_decode  from 'jwt-decode';

const UserContext = createContext();

const AuthProvider = ({children})=>{
 const[user, setUser] = useState(() => storage.load(storage.keys.auth)? jwt_decode(storage.load(storage.keys.auth)):null  )
 const[accesToken, setAccessToken] = useState(null)
 const dispatch = useDispatch()

 const loginUser = (data) => {
   dispatch(services.loginAuth(data));
 };


const data = {
  loginUser,
  setAccessToken,
  setUser,
  user
};
return (
    <UserContext.Provider value={data}>{children}</UserContext.Provider>
)

};
 export {AuthProvider}
 export default UserContext;
