const header = () => {
  let options = {
    "Content-Type": "application/json",
    Accept: "application/json",
  };
  let token = load(keys.auth);
  if (token) {
    let tokenhead = {
      Autorization: "Bearer " + token,
    };
    const header = { ...options, ...tokenhead };
    return header;
  }
};
export default { get, post, put, del };
