import { LOAD, CREATE, UPDATE, DELETE, CLEAR } from "types";

const initialstate = {
  choferes: [],
};

export default (state = initialstate, action) => {
  switch (action.type) {
    case LOAD: {
      return {
        ...state,
        choferes: [ ...action.payload],
      };
    }
    case CREATE: {
      return {
        ...state,
        choferes: [...state.choferes, action.payload],
      };
    }
    case UPDATE: {
      let newArray = state.choferes.map((chofer) =>
        chofer.id === action.payload.id ? action.payload : chofer
      );

      return {
        ...state,
        choferes: newArray,
      };
    }

    case DELETE: {
      return {
        ...state,
        choferes: state.choferes.map((chofer) => chofer.id !== action.payload),
      };
    }
    case CLEAR: {
      return initialstate;
    }

    default:
      return state;
  }
};
