import React, { useContext, useEffect, useState } from "react";
import { Col, Container, Form, Row, Button } from "react-bootstrap";
import OmnibusContext from "context/OmnibusContext";
import useOmnibus from "hooks/useOmnibus";


const objnew = {
  pk: null,
  numero: "",
  chapa: "",
  disponible: false,
  capacidad: "",
  create_by: Number(1),
};

const validationsForm = (form) => {
  let errors = {};
  let regexName = /^[A-Za-zÑñÁáÉéÍíÓóÚúÜü\s]+$/;
  let regexEmail = /^(\w+[/./-]?){1,}@[a-z]+[/.]\w{2,}$/;
  let regexComments = /^.{1,255}$/; 

  if (!form.numero.trim()) {
    errors.numero = "El campo 'Numero' es requerido";
  } else if (!regexName.test(form.numero.trim())) {
    errors.numero = "El campo 'Nombre' sólo acepta letras y espacios en blanco";
  }

  if (!form.email.trim()) {
    errors.email = "El campo 'Email' es requerido";
  } else if (!regexEmail.test(form.email.trim())) {
    errors.email = "El campo 'Email' es incorrecto";
  }

  if (!form.subject.trim()) {
    errors.subject = "El campo 'Asunto a tratar' es requerido";
  }

  if (!form.comments.trim()) {
    errors.comments = "El campo 'Comentarios' es requerido";
  } else if (!regexComments.test(form.comments.trim())) {
    errors.comments =
      "El campo 'Comentarios' no debe exceder los 255 caracteres";
  }

  return errors;
};

let styles = {
  fontWeight: "bold",
  color: "#dc3545",
};
const FormOmnibus = () => {
  const { form, handleChange, handleChangeCkeck, handleSubmit, setModalClose } =
    useOmnibus(initialForm, validationsForm);
  return (
    <>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Numero del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  name="numero"
                  value={form.numero}
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Chapa del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  value={form.chapa}
                  name="chapa"
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Capacidad del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  value={form.capacidad}
                  name="capacidad"
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Check
                type="switch"
                label="Disponibilidad del Omnibus"
                name="disponible"
                checked={form.disponible}
                onChange={handleChangeCkeck}
                id="disponible"
              ></Form.Check>
            </Col>
          </Row>
          <Row>
            <Button
              className="btn-fill ml-auto mr-2"
              variant="danger"
              onClick={setModalClose}
            >
              Cancelar
            </Button>
            <Button className="btn-fill " type="submit" variant="info">
              Acceptar
            </Button>
          </Row>
        </Form>
      </Container>
    </>
  );
};

export default FormOmnibus;
