import React, { useEffect, useContext, useState } from "react";
import ViajeContext from "../../context/ViajesContext";
import { Col, Container, Form, Row , Button} from "react-bootstrap";

const objnew = {
  pk: null,
  hora: "",
  omnibus: "",
  chofer1: "",
  chofer2: false,
  origen: "",
  destino: "",
  create_by: Number(1),
};

const FormViajes = () => {
  const { create, update, dataToEdit,omnibus, destinos, choferes, setdataToEdit, setModalClose } =
    useContext(ViajeContext);
  const [form, setForm] = useState(objnew);

  useEffect(() => {
    if (dataToEdit) {
      setForm(dataToEdit);
    } else {
      setForm(objnew);
    }
  }, [dataToEdit]);

  const handleChange = (event) => {
    setForm({
      ...form,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!form.hora || !form.omnibus) {
      alert("NO se admiten campos en blanco");
      return;
    }
    if (objnew.pk === null) {
      create(form);
    } else {
      update(form);
    }
    handleReset();
  };

  const handleReset = () => {
    setForm(objnew);
    setdataToEdit(null);
    setModalClose();
  };

  return (
    <>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Hora</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  name="hora"
                  value={form.hora}
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Numero del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  name="omnibus"
                  onChange={handleChange}
                >
                  {omnibus.map((omnibu) => (
                    <option value={omnibu.pk}>{omnibu.chapa}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Chofer 1 </label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  onChange={handleChange}
                >
                  {choferes.map((chofer) => (
                    <option value={chofer.pk}>{chofer.nombre}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Chofer 2</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  onChange={handleChange}
                >
                  {choferes.map((chofer) => (
                    <option value={chofer.pk}>{chofer.chapa}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Origen</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  onChange={handleChange}
                >
                  {destinos.map((destino) => (
                    <option value={destino.pk}>{destino.nombre}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
            <Col className="px-1 col col-md-6">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Destino</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  onChange={handleChange}
                >
                  {destinos.map((destino) => (
                    <option value={destino.pk}>{destino.nombre}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Button
              className="btn-fill ml-auto mr-2"
              variant="danger"
              onClick={setModalClose}
            >
              Cancelar
            </Button>
            <Button className="btn-fill " type="submit" variant="info">
              Acceptar
            </Button>
          </Row>
        </Form>
      </Container>
    </>
  );
};

export default FormViajes;
