import React, { useContext, useEffect, useState } from "react";
import OmnibusContext from "context/OmnibusContext";



const useOmnibus = (initialForm, validationsForm) => {
  const { dataToEdit, setdataToEdit, setModalClose, create, update } =
    useContext(OmnibusContext);
  const [form, setForm] = useState(objnew);


  useEffect(() => {
    console.log("ver datatoEdit", dataToEdit);
    if (dataToEdit) {
      setForm(dataToEdit);
    } else {
      setForm(objnew);
    }
  }, [dataToEdit]);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleChangeCkeck = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.checked,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.numero || !form.chapa) {
      alert("Los campos esta vacios");
      return;
    }
    if (form.pk === null) {
      delete form.pk;
      create(form);
    } else {
      update(form);
    }
    handleReset();
  };

  const handleReset = (e) => {
    setForm(objnew);
    setdataToEdit(null);
    setModalClose();
  };

  return {
    form,
    handleChange,
    handleChangeCkeck,
    handleSubmit,
    handleReset,
    setModalClose,
  };
};

export default useOmnibus;
