import React, { useContext } from "react";
import { Button } from "react-bootstrap";
import OmnibusContext from "context/OmnibusContext";

const TableOmnibusBodyRow = ({ om }) => {
  let { pk, numero, chapa, disponible, capacidad } = om;

  const { setdataToEdit, setModalShow, setdataToDel, setDataToDelShow } =
    useContext(OmnibusContext);

  const selectData = () => {
    setdataToEdit(om);
    setModalShow();
  };
  const delData = () => {
    setdataToDel(om);
    setDataToDelShow();
  };

  return (
    <>
      <tr>
        <td>{pk}</td>
        <td>{numero}</td>
        <td>{chapa}</td>
        <td>{disponible ? "Disponible" : "Taller"}</td>
        <td>{capacidad}</td>
        <td>
          <>
            <Button
              onClick={() => selectData()}
              type="Submit"
              className="btn-fill "
              variant="info"
            >
              Editar
            </Button>{" "}
            <Button
              type="submit"
              onClick={() => delData()}
              className="btn-fill "
              variant="danger"
            >
              Eliminar
            </Button>
            {disponible
             ? (
              <Button
                onClick={() => selectData()}
                type="Submit"
                className="btn-fill "
                variant="info"
              >
                Taller
              </Button>
            ) : (
              <Button
                type="submit"
                onClick={() => delData()}
                className="btn-fill "
                variant="danger"
              >
                Disponible
              </Button>
            )}
          </>
        </td>
      </tr>
    </>
  );
};

export default TableOmnibusBodyRow;
