import React, { useState, useEffect } from "react";
import { Card, Col, Container, Form, Row, Button } from "react-bootstrap";
import useChofer from "hooks/useChofer";

const objnew = {
  pk: null,
  nombre: "",
  ci: "",
  url: "",
  create_by: Number(1),
  members: [],
};
const validationsForm = (form, setDisable) => {
  let errores = {};
  let regexName = /^[A-Za-zÑñÁáÉéÍíÓóÚúÜü\s]+$/;
  let regexNumero = /^[0-9].{1,4}$/;
  let regexChapa = /^[A-Z][0-9][0-9][0-9][0-9][0-9][0-9]$/;
  let regexEmail = /^(\w+[/./-]?){1,}@[a-z]+[/.]\w{2,}$/;
  let regexComments = /^.{1,255}$/;

  if (!form.nombre.trim()) {
    errores.numero = "El campo 'Numero' es requerido";
  } else if (!regexNumero.test(form.numero.trim())) {
    errores.numero = "El campo 'Numero' es un Numero hasta 4 digitos ";
  }

  if (!form.chapa.trim()) {
    errores.chapa = "El campo 'Chapa' es requerido";
  } else if (!regexChapa.test(form.chapa.trim())) {
    errores.chapa = "El campo 'Chapa' no es una chapa valida Ej C234560 ";
  }

  if (!form.capacidad.trim()) {
    errores.capacidad = "El campo 'Capacidad' es requerido";
  } else if (!regexNumero.test(form.capacidad.trim())) {
    errores.capacidad = "El campo 'Capacidad' es un Numero";
  }

  if (Object.keys(errores).length === 0) {
    setDisable(false);
  } else {
    setDisable(true);
  }

  return errores;
};

let styles = {
  fontWeight: "bold",
  color: "#dc3545",
};

const FormChoferes = ({
  dataToEdit,
  setdataToEdit,
  setModalClose,
  create,
  update,
}) => {
  const { form, errores, handleChange, handleSubmit, handleBlur, handleReset } =
    useChofer(objnew, dataToEdit, setdataToEdit, setModalClose, create, update, validationsForm);

  return (
    <>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Nombre del Chofer</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  name="nombre"
                  value={form.nombre}
                  onChange={handleChange}
                  onBlur={handleBlur}
                ></Form.Control>
              </Form.Group>
              {errores.nombre && <span style={styles}>{errores.nombre}</span>}
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">CI del Chofer</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  value={form.ci}
                  name="ci"
                  onChange={handleChange}
                  onBlur={handleBlur}
                ></Form.Control>
              </Form.Group>
              {errores.ci && <span style={styles}>{errores.ci}</span>}
            </Col>
          </Row>
          <Row>
            <Button
              //style={{ marginLeft: "300px" }}
              className="btn-fill ml-auto mr-2"
              variant="danger"
              onClick={handleReset}
            >
              Cancelar
            </Button>
            <Button
              className="btn-fill pull-right"
              type="submit"
              variant="info"
            >
              Acceptar
            </Button>
          </Row>
        </Form>
      </Container>
    </>
  );
};

export default FormChoferes;
