import React, { useContext, useEffect, useState } from "react";
import {  Col, Container, Form, Row, Button ,} from "react-bootstrap";
import OmnibusContext from "context/OmnibusContext";

const objnew = {
  pk: null,
  numero: "",
  chapa: "",
  disponible: false,
  capacidad :"",
  create_by: Number(1),

};

const FormOmnibus = () => {
  const { dataToEdit, setdataToEdit, setModalClose, create, update } =
    useContext(OmnibusContext);
  const [form, setForm] = useState(objnew);

  useEffect(() => {
    console.log("ver datatoEdit", dataToEdit);
    if (dataToEdit) {
      setForm(dataToEdit);
    } else {
      setForm(objnew);
    }
  }, [dataToEdit]);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleChangeCkeck = (e) =>{
     setForm({
       ...form,
       [e.target.name]: e.target.checked,
     });
  }


  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.numero || !form.chapa) {
      alert("Los campos esta vacios");
      return;
    }
    if (form.pk === null) {
      delete form.pk;
      create(form);
    } else {
      update(form);
    }
    handleReset();
  };

  const handleReset = (e) => {
    setForm(objnew);
    setdataToEdit(null);
    setModalClose();
  };
  return (
    <>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Numero del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  name="numero"
                  value={form.numero}
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Chapa del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  value={form.chapa}
                  name="chapa"
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Capacidad del Omnibus</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  value={form.capacidad}
                  name="capacidad"
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Check
                type="switch"
                label="Disponibilidad del Omnibus"
                name="disponible"
                checked={form.disponible}
                onChange={handleChangeCkeck}
                id="disponible"
              ></Form.Check>
            </Col>
          </Row>
          <Button
            style={{ marginLeft: "300px" }}
            className="btn-fill pull-right"
            type="submit"
            variant="info"
          >
            Acceptar
          </Button>
          <Button
            style={{ marginLeft: "auto" }}
            className="btn-fill"
            variant="danger"
            onClick={setDataToDelClose}
          >
            Cancelar
          </Button>
        </Form>
      </Container>
    </>
  );
};

export default FormOmnibus;
