import React, { useState, useEffect, useContext } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import {
  Container,
  Button,
  Form,
  Col,
  Row,
  Breadcrumb,
  ButtonToolbar,
} from "react-bootstrap";
import "./grid.css";
import { useHistory } from "react-router-dom";
import servicesviajes from "modules/Viajes/services";
import ReservacionContext from "context/ReservacionesContext";
import useReservciones from '../../hooks/useReservciones';

let reserva = {
  fecha: "",
  viajes: "",
  create_by: Number(1),
  members: [1],
  cis: "",
  asientos: "",
};
const validationsForm = (form, setDisable) => {
  let errores = {};
  let regexName = /^[A-Za-zÑñÁáÉéÍíÓóÚúÜü\s]+$/;
  let regexNumero = /^[0-9].{3}$/;
  let regexCapacidad = /^[0-9].{1}$/;
  let regexChapa = /^[A-Z][0-9][0-9][0-9][0-9][0-9][0-9]$/;
  let regexEmail = /^(\w+[/./-]?){1,}@[a-z]+[/.]\w{2,}$/;
  let regexComments = /^.{1,255}$/;

  console.log("ver form importante", form);
  if (!form.numero === "") {
    errores.numero = "El campo 'Numero' es requerido";
  } else if (!regexNumero.test(form.numero)) {
    errores.numero = "El campo 'Numero' es un Numero hasta 4 digitos ";
  }

  if (!form.chapa === "") {
    errores.chapa = "El campo 'Chapa' es requerido";
  } else if (!regexChapa.test(form.chapa)) {
    errores.chapa = "El campo 'Chapa' no es una chapa valida Ej C234560 ";
  }

  if (!form.capacidad === "") {
    errores.capacidad = "El campo 'Capacidad' es requerido";
  } else if (!regexCapacidad.test(form.capacidad)) {
    errores.capacidad = "El campo 'Capacidad' es un Numero";
  }

  if (Object.keys(errores).length === 0) {
    setDisable(false);
  } else {
    setDisable(true);
  }

  return errores;
};

let styles = {
  fontWeight: "bold",
  color: "#dc3545",
};
const RealizarReservacion = () => {
  const {
    cantidad,
    viajes,
    res,
    cant,
    hanledsubmit,
    handleChange,
    handleChangeCIS,
    Regresar,
  } = useReservciones(reserva, validationsForm);
  return (
    <Container>
      <Breadcrumb>
        <Breadcrumb.Item href="#">Home</Breadcrumb.Item>
        <Breadcrumb.Item href="https://getbootstrap.com/docs/4.0/components/breadcrumb/">
          Libra
        </Breadcrumb.Item>
        <Breadcrumb.Item active>Data</Breadcrumb.Item>
      </Breadcrumb>
      <div>
        <p>
          <b>{`Desean Viajar ${cant} personas.`}</b>
        </p>
      </div>
      <Form onSubmit={hanledsubmit}>
        <Row>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Fecha</b>
              </Form.Label>
              <Form.Control
                type="date"
                name="fecha"
                value={res.fecha}
                onChange={handleChange}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          </Col>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Viaje</b>
              </Form.Label>
              <Form.Control
                as="select"
                name="viajes"
                onChange={handleChange}
                placeholder="Entre La fecha"
              >
                <option value="">------</option>
                {viajes.map((viaje, index) => (
                  <option key={index} value={viaje.pk}>
                    {`${viaje.origen}- ${viaje.destino}`}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
        </Row>

        <div className="grid">
          {cantidad.map((cant, index) => (
            <Form.Group key={index}>
              <Form.Label className="label">
                <b>CI {index + 1}:</b>
              </Form.Label>
              <Form.Control
                name="cis"
                onChange={handleChangeCIS}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          ))}
        </div>
        <Row>
          <>
            <Button
              className="btn-fill ml-auto mr-2"
              variant="danger"
              //onClick={setModalClose}
              onClick={() => Regresar()}
            >
              Cancelar
            </Button>{" "}
          </>

          <Button className=" btn btn-fill " type="submit" variant="info">
            Acceptar
          </Button>
        </Row>
      </Form>
    </Container>
  );
};

export default RealizarReservacion;
