import React, { useEffect, useContext, useState } from "react";
import ViajeContext from "../../context/ViajesContext";
import { Col, Container, Form, Row } from "react-bootstrap";

const objnew = {
  pk: null,
  hora: "",
  omnibus: "",
  chofer1: "",
  chofer2: false,
  origen: "",
  destino: "",
  create_by: Number(1),
};

const FormViajes = () => {
  const { create, update, dataToEdit, setdataToEdit, setModalClose } =
    useContext(ViajeContext);
  const [form, setForm] = useState(objnew);

  useEffect(() => {
    if (dataToEdit) {
      setForm(dataToEdit);
    } else {
      setForm(objnew);
    }
  }, [dataToEdit]);

  const handleChange = (event) => {
    setForm({
      ...form,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!form.hora || !form.omnibus) {
      alert("NO se admiten campos en blanco");
      return;
    }
    if (objnew.pk === null) {
      create(form);
    } else {
      update(form);
    }
    handleReset();
  };

  const handleReset = () => {
    setForm(objnew);
    setdataToEdit(null);
    setModalClose();
  };

  return (
    <>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">Hora</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  type="text"
                  name="hora"
                  value={form.hora}
                  onChange={handleChange}
                ></Form.Control>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col className="px-1 col col-md-12">
              <Form.Group>
                <label htmlFor="nombre del Chofer">asdasdad</label>
                <Form.Control
                  placeholder="Nombre del Chofer."
                  as="select"
                  onChange={handleChange}
                >
                  <option value="1">Numero1</option>
                  <option value="2">Numero2</option>
                  <option value="3">Numer0 3</option>
                  <option value="4">Numero4</option>
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
        </Form>
      </Container>
    </>
  );
};

export default FormViajes;
