import React,{useState, useEffect, useContext} from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { Container, Button, Form, Col, Row } from "react-bootstrap";
import "./grid.css";
import { useHistory } from "react-router-dom";
import servicesviajes from "modules/Viajes/services";
import  ReservacionContext  from 'context/ReservacionesContext';

let reserva={
  fecha:"",
  viajes:"",
  create_by:Number(1),
  members:[1],
  cis:"",
  asientos:""
}
const RealizarReservacion = () => {
  const[res,setRes]=useState(reserva)
  const[cis,setCis]=useState([])
  const { cant } = useParams();
  const state = useSelector((state) => state);
  const { cantidad } = state.reservacion;
  const dispatch = useDispatch()

  console.log("ver cantidad", cantidad);
   let hsitory = useHistory()

  const Regresar=()=>{
      hsitory.push("/admin/reservar");
  }

  useEffect(() => {
      dispatch()
  }, [])


   const handleChangeCIS = (e) => {
       e.preventDefault()
       cis.push(e.target.value);

   };

  const handleChange=(e)=>{
    res.asientos = Number(cant);

    setRes({
      ...res,
      [e.target.name]:e.target.value
    })

  }

  const hanledsubmit=(e)=>{
      e.preventDefault()
      console.log("se envio el sumit", cis);
      res.cis = cis.join(";");
      console.log("se envio el sumit", res);
  }
  return (
    <Container>
      <div>
        <p>
          <b>{`Desean Viajar ${cant} personas.`}</b>
        </p>
      </div>
      <Form onSubmit={hanledsubmit}>
        <Row>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Hora</b>
              </Form.Label>
              <Form.Control
                name="fecha"
                value={res.fecha}
                onChange={handleChange}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          </Col>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Viaje</b>
              </Form.Label>
              <Form.Control
                as="select"
                name="viajes"
                onChange={handleChange}
                placeholder="Entre La fecha"
              >
                {[1,2,3,4,5,6].map((viaje, index) => (
                  <option pk={index} value={viaje.pk}>{viaje}</option>

                ))}
              </Form.Control>
            </Form.Group>
          </Col>
        </Row>

        <div className="grid">
          {cantidad.map((cant, index) => (
            <Form.Group key={index}>
              <Form.Label className="label">
                <b>CI {index + 1}:</b>
              </Form.Label>
              <Form.Control
                name="cis"
                onChange={handleChangeCIS}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          ))}
        </div>
        <Row>
          <Button
            className="btn-fill ml-auto mr-2"
            variant="danger"
            //onClick={setModalClose}
            onClick={() => Regresar()}
          >
            Cancelar
          </Button>
          <Button className="btn-fill " type="submit" variant="info">
            Acceptar
          </Button>
        </Row>
      </Form>
    </Container>
  );
};

export default RealizarReservacion;
