import { LOAD_VIAJES, CREATE_VIAJES , } from "../types/index";

let initialstate = {
  viajes: [],
};

 const viajesReducers = (state = initialstate, action) => {
  switch (action.type) {
    case LOAD_VIAJES: {
      return {
        ...state,
        viajes: state.viajes.map((v) => v),
      };
    }
    case CREATE_VIAJES: {
      return {
        ...state,
        viajes: [...state.viajes, action.payload],
      };
    }

    case UPDATE_VIAJES: {
      let newData = state.viajes.map((viaje) =>
        viaje.pk === action.payload.pk ? action.payload : viaje
      );
      return {
        ...state,
        viajes: newData,
      };
    }
    case DELETE_VIAJES: {
      let newDataDel = state.viajes.filter(
        (viaje) => viaje.pk !== action.payload.pk
      );
      return {
        ...state,
        viajes: newDataDel,
      };
    }
    case CLEAR_VIAJES: {
      return initialstate;
    }
    default:
      state;
  }
};
 export default viajesReducers;
