import React, { useState, useEffect, useContext } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import {
  Container,
  Button,
  Form,
  Col,
  Row,
  Breadcrumb,
  ButtonToolbar,
} from "react-bootstrap";
import "./grid.css";
import { useHistory } from "react-router-dom";
import servicesviajes from "modules/Viajes/services";
import ReservacionContext from "context/ReservacionesContext";
import useReservciones from '../../hooks/useReservciones';

let reserva = {
  fecha: "",
  viajes: "",
  create_by: Number(1),
  members: [1],
  cis: "",
  asientos: "",
};
const RealizarReservacion = () => {
  const {} = useReservciones(r)
  return (
    <Container>
      <Breadcrumb>
        <Breadcrumb.Item href="#">Home</Breadcrumb.Item>
        <Breadcrumb.Item href="https://getbootstrap.com/docs/4.0/components/breadcrumb/">
          Libra
        </Breadcrumb.Item>
        <Breadcrumb.Item active>Data</Breadcrumb.Item>
      </Breadcrumb>
      <div>
        <p>
          <b>{`Desean Viajar ${cant} personas.`}</b>
        </p>
      </div>
      <Form onSubmit={hanledsubmit}>
        <Row>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Fecha</b>
              </Form.Label>
              <Form.Control
                type="date"
                name="fecha"
                value={res.fecha}
                onChange={handleChange}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          </Col>
          <Col xs={12} md={6}>
            <Form.Group>
              <Form.Label className="label">
                <b>Viaje</b>
              </Form.Label>
              <Form.Control
                as="select"
                name="viajes"
                onChange={handleChange}
                placeholder="Entre La fecha"
              >
                <option value="">------</option>
                {viajes.map((viaje, index) => (
                  <option key={index} value={viaje.pk}>
                    {`${viaje.origen}- ${viaje.destino}`}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
        </Row>

        <div className="grid">
          {cantidad.map((cant, index) => (
            <Form.Group key={index}>
              <Form.Label className="label">
                <b>CI {index + 1}:</b>
              </Form.Label>
              <Form.Control
                name="cis"
                onChange={handleChangeCIS}
                placeholder="Entre La fecha"
              ></Form.Control>
            </Form.Group>
          ))}
        </div>
        <Row>
          <>
            <Button
              className="btn-fill ml-auto mr-2"
              variant="danger"
              //onClick={setModalClose}
              onClick={() => Regresar()}
            >
              Cancelar
            </Button>{" "}
          </>

          <Button className=" btn btn-fill " type="submit" variant="info">
            Acceptar
          </Button>
        </Row>
      </Form>
    </Container>
  );
};

export default RealizarReservacion;
