import React, { useState, useContext } from "react";
import { Container, Form, Row, Col, Button } from "react-bootstrap";
import { useHistory } from 'react-router-dom';
import ReservacionContext from "context/ReservacionesContext";
import getReservacionesxViaje from "./servicesreservar";
const Reservar = () => {
  const[cant, setCant] = useState(null)
   // const {cantPersonViajar} = useContext(ReservacionContext) 
   let history = useHistory()


   const  handleChange=(e)=>{
     setCant(e.target.value)

     }

  const handleSubmit =()=>{
       getReservacionesxViaje(cant);
      history.push(`/admin/makeReservation/${cant}/`);
  }

  return (
    <Container>
      <Form onSubmit={()=>handleSubmit()}>
        <Row className="align-items-center">
          <Col xs="10">
            <Form.Group as={Col} controlId="formGridEmail">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="number"
                min="1"
                placeholder="Personas a Viajar"
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col xs="2">
            <Form.Group as={Col} className="mt-4">
              <Button variant="info" className="btn-fill" type="submit">
                Aceptar
              </Button>
            </Form.Group>
          </Col>
        </Row>
      </Form>
    </Container>
  );
};

export default Reservar;
